import React,{useEffect, useState} from 'react'
import Header from '../components/common/Header'
import { getCart, getSubtotal } from '../reducks/carts/selectors'
import {useSelector, useDispatch} from 'react-redux'
import  {fetchItem} from "../reducks/items/operations"
import { fetchCart} from "../reducks/carts/operations"
import { addOrder } from '../reducks/orders/operations'
import { useNavigate } from 'react-router-dom'
import {clearCheckoutOrderErrorAction} from '../reducks/orders/actions'
import { getUser } from '../reducks/users/selectors'


function Orders() {
  const navigate = useNavigate();
  const user = useSelector(getUser)
  console.log(user.name)
    const dispatch = useDispatch()
    useEffect(()=>{
      dispatch(fetchItem())
      if (localStorage.getItem("LOGIN_USER_KEY")){
        dispatch(fetchCart())
      }

  },[])
    const carts = useSelector(getCart)
    let totalQty =0



    if(carts.length > 0 ){
        carts.map(cart=>{
            {totalQty += cart.quantity}
        })
    }


    // const [name,setName] = useState('')
    // const [num,setPhoneNum] = useState()
    // const [address,setAddress] = useState()
    // const [code,setCode] = useState()
    // const [apt,setApt] = useState()
    // const [city,setCity] = useState()
    // const [state,setState] = useState()

    const initialValues = {
      customer_name: user.name,
      phone:"",
      address:"",
      pin_code:"",
      apt:"",
      city:"",
      state:"",
    }

    const [values,setValues] = useState(initialValues)
    const handleInputChange = (e) => {
      const {name,value} = e.target;

      setValues({
        ...values,
        [name]:value,
      })
    }

    const handleClick = () => {
        dispatch(addOrder())
        navigate('/thanks')
    }

    const subtotal = useSelector(getSubtotal)

  return (
  <>
      <Header />

      <div class="menu delivery">
        <hr />
        <h2>Order your items</h2>
        <hr />

      </div>

      <div class="delivery-details">
        <h3>Delivery Details</h3>
        <p>Please check your items and confirm it</p>
        <table>
            {carts && carts.length > 0 && carts.map((cart)=>

            <tr>

            <td>{cart.item.name}</td>
            <td class="number">{cart.quantity}</td>
            <td class="price">${cart.quantity * cart.item.price}</td>
        </tr>
            )}

            <tr class="table-bottom">
                <td>Total price</td>
                <td class="number">{totalQty}</td>
                <td class="price">${subtotal}</td>
            </tr>


        </table>
        <div class="form">
        <label>Full Name</label>
        <input type="text" onChange={handleInputChange} value={values.customer_name} class="input-box" required name="customer_name"/>
        <br/>
        <label>Phone Number</label>
        <input type="Number" onChange={handleInputChange} value={values.phone} class="input-box" name="phone"/>
        <br/>
        <label>Street address or P.O. Box</label>
        <input type="text" onChange={handleInputChange} value={values.address} class="input-box" name="address"/>
        <br/>
        <label>PIN code</label>
        <input type="number" onChange={handleInputChange} value={values.pin_code} class="input-box" name="pin_code"/>
        <br/>
        <label>Apt, suite, unit, building, floor, etc</label>
        <input type="text" onChange={handleInputChange} value={values.apt} class="input-box" />
        <br/>
        <label>City</label>
        <input type="text" onChange={handleInputChange} value={values.city} class="input-box" name="city"/>
        <br/>
        <label>State</label>
        <input type="text" onChange={handleInputChange} value={values.state} class="input-box" name="state"/>
        <br/>
        <button  onClick={handleClick}>Submit</button>
        </div>
        </div>
 </>
 )}


   export default Orders
