import API from "../../API"
import { addOrderAction } from "./actions"



const api = new API()
export const addOrder = (params) =>{
    return async dispatch=>{
        return api
        .orderAdd(params)
        .then((orders) => {

            dispatch(addOrderAction(orders))
        })   
        .catch((error) => {
            alert("Failed to connect API carts")
        })
    }}
