import initialState from "../store/initialState";
import * as Actions from "./actions"

export const cartReducer = (state = initialState.carts, {payload, subtotal, type})=>{
    switch (type){
        case Actions.FETCH_CART:
            return{
                list:payload,
                subtotal:subtotal
            }
            case Actions.ADD_CART:
            return{
                list:payload,
                subtotal:subtotal
            }
            case Actions.INCREASE_CART:
            return{
                list:payload,
                subtotal:subtotal
            }
            case Actions.DECREASE_CART:
            return{
                list:payload,
                subtotal:subtotal
            }
            default:return state


    }
}
