export const FETCH_CART = 'FETCH_CART'
export const fetchCartAction  = (carts, subtotal) =>{
   return {
    type:'FETCH_CART',
    payload:carts,
    subtotal: subtotal
   }
}


export const ADD_CART = 'ADD_CART'
export const addCartAction  = (carts, subtotal) =>{
   return {
    type:'ADD_CART',
    payload:carts,
    subtotal: subtotal
   }
}


export const INCREASE_CART = 'INCREASE_CART'
export const CartAction  = (carts, subtotal) =>{
   return {
    type:'INCREASE_CART',
    payload:carts,
    subtotal: subtotal
   }
}
