export const ADD_ORDER = 'ADD_ORDER'
export const addOrderAction  = (orders) =>{
   return {
    type:'ADD_ORDER',
    payload:orders,
   }
}

export const CLEARCHECKOUT_ORDER_ERROR = "CHECKOUT_ORDER_ERROR";
export const checkoutOrderErrorAction = (errors) => {
   return {
      type:CHECKOUT_ORDER_ERROR,
      payload: {
         errors,
      },
   }
}
