import API from "../../API"
import { addOrderAction,checkoutOrderErrorAction } from "./actions"



const api = new API()
export const addOrder = (addCartBody, onSuccess = null) =>{
    return async dispatch=>{
        return api
        .orderAdd(addCartBody)
        .then(() => {

            dispatch(addOrderAction());
            onSuccess();
        })   
        .catch((error) => {
            dispatch(checkoutOrderErrorAction(error.res))
        })
    }}
