import React,{useState} from 'react'
import { useSelector } from 'react-redux'
import { getSubtotal } from '../../reducks/carts/selectors'
import { Link, useParams } from 'react-router-dom'
import { useEffect } from 'react'

export default function Footer({setBtn,btn}) {
  const subTotal = useSelector((state)=>state.carts.subtotal)
  const [url, setUrl] = useState('')
  let {abc} = useParams()
  useEffect(()=>{
    setUrl(abc)
    console.log(url)
  }, [abc])

  return (
    <>
     <footer>
    <div class="total">
      <h2>Subtotal: ${subTotal}</h2>
      <Link to = "/carts">
      <button class="add-cart"onClick={()=> setBtn(false)}>{btn ? 'Check selected items': checkout}</button>
      </Link>
    </div>
  </footer>

    </>
  )
}
